package basic

import (
	"fmt"

	"github.com/tdewolff/canvas"
)

func mustLoadGameAsset(name string) *canvas.Path {
	path, err := loadGameAsset(name)
	if err != nil {
		panic(err)
	}
	return path
}

func loadGameAsset(name string) (*canvas.Path, error) {
	return loadAsset(name)
}

func loadAsset(name string) (*canvas.Path, error) {
	svgData, ok := svgStrings[name]
	if !ok {
		return nil, fmt.Errorf("invalid key %s", name)
	}

	path, err := canvas.ParseSVGPath(string(svgData))
	if err != nil {
		return nil, fmt.Errorf("parsing file: %w", err)
	}

	return path, nil
}

